#!/bin/python3import sysimport zlibstdinb = sys.stdin.bufferstdoutb = sys.stdout.bufferinput = stdinb.read()if sys.argv[1] == 'd':	d = zlib.decompressobj(-zlib.MAX_WBITS)	output = d.decompress(input)	output += d.flush()else:	c = zlib.compressobj(zlib.Z_DEFAULT_COMPRESSION, zlib.DEFLATED, -zlib.MAX_WBITS, zlib.DEF_MEM_LEVEL, zlib.Z_DEFAULT_STRATEGY)	output = c.compress(input)	output += c.flush()stdoutb.write(output)